import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import CartModal from '../shop/CartModal';
import { Menu, X, ShoppingCart } from 'lucide-react';

function Header() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { getItemCount } = useCart();

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="bg-gray-800 text-white">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link to="/" className="text-xl font-bold">
            Charr√∫as Hockey
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex space-x-4">
            <Link to="/" className="hover:text-gray-300">Inicio</Link>
            <Link to="/alta" className="hover:text-gray-300">Alta de Productos</Link>
            <Link to="/contacto" className="hover:text-gray-300">Contacto</Link>
            <Link to="/nosotros" className="hover:text-gray-300">Nosotros</Link>
          </nav>

          {/* Cart Button */}
          <div className="flex items-center gap-4">
            <button 
              onClick={toggleModal}
              className="flex items-center gap-2 bg-blue-600 px-4 py-2 rounded-lg hover:bg-blue-700"
            >
              <ShoppingCart size={20} />
              <span>{getItemCount()}</span>
            </button>

            {/* Mobile Menu Button */}
            <button 
              className="md:hidden"
              onClick={toggleMobileMenu}
            >
              {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <nav className="md:hidden py-4">
            <div className="flex flex-col space-y-2">
              <Link 
                to="/" 
                className="hover:bg-gray-700 px-3 py-2 rounded"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Inicio
              </Link>
              <Link 
                to="/alta" 
                className="hover:bg-gray-700 px-3 py-2 rounded"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Alta de Productos
              </Link>
              <Link 
                to="/contacto" 
                className="hover:bg-gray-700 px-3 py-2 rounded"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Contacto
              </Link>
              <Link 
                to="/nosotros" 
                className="hover:bg-gray-700 px-3 py-2 rounded"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Nosotros
              </Link>
            </div>
          </nav>
        )}
      </div>

      {/* Cart Modal */}
      {isModalOpen && (
        <CartModal
          isVisible={isModalOpen}
          closeModal={() => setIsModalOpen(false)}
        />
      )}
    </header>
  );
}

export default Header;
