import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import CartModal from '../shop/CartModal';
import logo from '../../assets/images/logo (2).png';

function Header() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const { cartItems } = useCart();

  return (
    <header className="bg-gray-800 text-white">
      <div className="container mx-auto px-4">
        {/* Primera secci√≥n: Logo y texto principal */}
        <div className="flex justify-center py-4">
          <div className="text-center">
            <img 
              src={logo} 
              alt="Logo de hockey shop" 
              className="h-48 w-auto mx-auto"
            />
            <h1 className="text-2xl font-bold mt-2">Charr√∫as Hockey Shop</h1>
            <p className="text-gray-300">Tu tienda especializada en equipamiento de hockey</p>
          </div>
        </div>

        {/* Segunda secci√≥n: Navegaci√≥n y carrito (con menos espacio) */}
        <div className="flex justify-between items-center py-2 border-t border-gray-700">
          <nav className="hidden md:flex space-x-6 text-lg">
            <Link to="/" className="hover:text-gray-300">Inicio</Link>
            <Link to="/alta" className="hover:text-gray-300">Alta de Productos</Link>
            <Link to="/contacto" className="hover:text-gray-300">Contacto</Link>
            <Link to="/nosotros" className="hover:text-gray-300">Nosotros</Link>
          </nav>

          <button 
            onClick={() => setIsModalOpen(!isModalOpen)}
            className="flex items-center space-x-2 bg-blue-600 px-6 py-2 rounded-lg hover:bg-blue-700 text-lg"
          >
            <span>üõí</span>
            <span>{cartItems.length}</span>
          </button>
        </div>
      </div>

      {isModalOpen && (
        <CartModal
          isVisible={isModalOpen}
          closeModal={() => setIsModalOpen(false)}
        />
      )}
    </header>
  );
}

export default Header;
