import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import { ChevronDown, ChevronUp } from 'lucide-react';

const ProductCard = ({ product }) => {
  const [showDetails, setShowDetails] = useState(false);
  const { addToCart } = useCart();

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300">
      <div className="aspect-w-3 aspect-h-2 relative">
        <img 
          src={product.imageUrl} 
          alt={product.name}
          className="w-full h-64 object-contain p-4"
        />
      </div>
      
      <div className="p-6">
        <h2 className="text-xl font-bold mb-2 text-gray-800">{product.name}</h2>
        <p className="text-gray-600 mb-4">
          {showDetails ? product.description : `${product.description.substring(0, 100)}...`}
        </p>
        
        <button
          onClick={() => setShowDetails(!showDetails)}
          className="flex items-center justify-center w-full text-gray-600 mb-4 hover:text-gray-800"
        >
          {showDetails ? (
            <>
              <ChevronUp className="mr-2" />
              Menos detalles
            </>
          ) : (
            <>
              <ChevronDown className="mr-2" />
              MÃ¡s detalles
            </>
          )}
        </button>

        <div className="flex justify-between items-center">
          <span className="text-2xl font-bold text-blue-600">
            ${product.price}
          </span>
          <button
            onClick={() => addToCart(product)}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors duration-300"
          >
            Agregar al carrito
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
