import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import CartModal from '../../components/shop/CartModal';

function Header() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const { cartItems } = useCart();

  return (
    <header className="bg-gray-800 text-white">
      <div className="container mx-auto px-4">
        {/* Primera secci√≥n: Logo y t√≠tulo */}
        <div className="flex items-center justify-between py-4">
          {/* Logo a la izquierda */}
          <div className="flex items-center">
            <img 
              src="/logo.png" 
              alt="Logo de hockey shop" 
              className="h-16 w-auto"
            />
            <span className="ml-4 text-2xl font-bold">Charr√∫as Hockey Shop</span>
          </div>

          {/* Navegaci√≥n y acciones */}
          <div className="flex items-center space-x-4">
            <nav className="hidden md:flex space-x-6">
              <a href="/" className="hover:text-gray-300">Inicio</a>
              <a href="/alta" className="hover:text-gray-300">Alta de Productos</a>
              <a href="/contacto" className="hover:text-gray-300">Contacto</a>
              <a href="/nosotros" className="hover:text-gray-300">Nosotros</a>
            </nav>

            <div className="flex items-center space-x-4">
              {/* Carrito */}
              <button 
                onClick={() => setIsModalOpen(!isModalOpen)}
                className="relative hover:text-gray-300"
                title="Carrito"
              >
                üõí
                {cartItems.length > 0 && (
                  <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full 
                    w-5 h-5 flex items-center justify-center text-xs">
                    {cartItems.length}
                  </span>
                )}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Modal de Carrito */}
      {isModalOpen && (
        <CartModal
          isVisible={isModalOpen}
          closeModal={() => setIsModalOpen(false)}
        />
      )}
    </header>
  );
}

export default Header;
