import React, { useEffect } from 'react';
import { useCart } from '../../context/CartContext';
import { X, Plus, Minus, ShoppingCart } from 'lucide-react';
import Button from '../common/Button';

const CartModal = ({ isVisible, closeModal }) => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity, 
    getTotal,
    clearCart 
  } = useCart();

  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === 'Escape') closeModal();
    };

    window.addEventListener('keydown', handleEsc);
    return () => window.removeEventListener('keydown', handleEsc);
  }, [closeModal]);

  const handleOutsideClick = (e) => {
    if (e.target.className.includes('modal-overlay')) {
      closeModal();
    }
  };

  if (!isVisible) return null;

  return (
    <div 
      className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex justify-center items-start pt-20"
      onClick={handleOutsideClick}
    >
      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-lg">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold flex items-center gap-2">
            <ShoppingCart size={24} />
            Carrito de Compras
          </h2>
          <button 
            onClick={closeModal}
            className="text-gray-500 hover:text-gray-700"
          >
            <X size={24} />
          </button>
        </div>

        {cartItems.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-500">El carrito está vacío</p>
          </div>
        ) : (
          <>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {cartItems.map((item) => (
                <div 
                  key={item.id} 
                  className="flex items-center justify-between p-4 bg-gray-50 rounded-lg"
                >
                  <div className="flex items-center space-x-4">
                    {item.imageUrl && (
                      <img 
                        src={item.imageUrl} 
                        alt={item.name}
                        className="w-16 h-16 object-cover rounded"
                      />
                    )}
                    <div>
                      <h3 className="font-medium">{item.name}</h3>
                      <p className="text-gray-600">
                        ${item.price} x {item.quantity} = ${item.price * item.quantity}
                      </p>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <button 
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        className="p-1 rounded-full hover:bg-gray-200"
                        disabled={item.quantity <= 1}
                      >
                        <Minus size={16} />
                      </button>
                      <span className="w-8 text-center">{item.quantity}</span>
                      <button 
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        className="p-1 rounded-full hover:bg-gray-200"
                      >
                        <Plus size={16} />
                      </button>
                    </div>
                    <button 
                      onClick={() => removeFromCart(item.id)}
                      className="text-red-500 hover:text-red-700"
                    >
                      <X size={20} />
                    </button>
                  </div>
                </div>
              ))}
            </div>

            <div className="border-t mt-4 pt-4">
              <div className="flex justify-between items-center mb-4">
                <span className="font-bold">Total:</span>
                <span className="font-bold text-xl">${getTotal()}</span>
              </div>

              <div className="space-y-2">
                <Button 
                  variant="primary"
                  fullWidth
                  onClick={() => {
                    // Aquí iría la lógica de checkout
                    alert('Procesando compra...');
                  }}
                >
                  Finalizar Compra
                </Button>
                <Button 
                  variant="secondary"
                  fullWidth
                  onClick={clearCart}
                >
                  Vaciar Carrito
                </Button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CartModal;
