import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useNotification } from '../context/NotificationContext';

const PaymentResult = () => {
  const { status } = useParams();
  const navigate = useNavigate();
  const { clearCart } = useCart();
  const { showNotification } = useNotification();

  useEffect(() => {
    const messages = {
      success: '¡Pago realizado con éxito!',
      pending: 'El pago está pendiente de confirmación',
      failure: 'Hubo un problema con el pago'
    };

    if (status === 'success') {
      clearCart();
    }

    showNotification(
      messages[status] || 'Estado de pago desconocido',
      status === 'success' ? 'success' : status === 'pending' ? 'info' : 'error'
    );
  }, [status, clearCart, showNotification]);

  return (
    <div className="payment-result">
      <div className="result-container">
        {status === 'success' && (
          <>
            <h2>¡Gracias por tu compra!</h2>
            <p>Tu pedido ha sido procesado correctamente.</p>
          </>
        )}
        {status === 'pending' && (
          <>
            <h2>Pago en Proceso</h2>
            <p>Tu pago está siendo procesado. Te notificaremos cuando se confirme.</p>
          </>
        )}
        {status === 'failure' && (
          <>
            <h2>Error en el Pago</h2>
            <p>Hubo un problema al procesar tu pago. Por favor, intenta nuevamente.</p>
          </>
        )}
        <button onClick={() => navigate('/')}>
          Volver a la tienda
        </button>
      </div>
    </div>
  );
};

export default PaymentResult;
