import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import { useNotification } from '../../context/NotificationContext';
import { ShoppingCart, ChevronDown, ChevronUp } from 'lucide-react';

const ProductCard = ({ product }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { addToCart } = useCart();
  const { showNotification } = useNotification();

  const handleAddToCart = () => {
    addToCart(product);
    showNotification(`${product.name} agregado al carrito`, 'success');
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="relative">
        <img 
          src={product.imageUrl} 
          alt={product.name}
          className="w-full h-48 object-cover"
        />
        {product.freeShipping && (
          <span className="absolute top-2 right-2 bg-green-500 text-white px-2 py-1 rounded-full text-sm">
            Envío gratis
          </span>
        )}
      </div>

      <div className="p-4">
        <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-2">{product.shortDescription}</p>
        <div className="flex justify-between items-center mb-4">
          <span className="text-xl font-bold">${product.price}</span>
          <span className="text-sm text-gray-500">Stock: {product.stock}</span>
        </div>

        <button
          onClick={handleAddToCart}
          disabled={product.stock === 0}
          className={`w-full py-2 px-4 rounded-lg flex items-center justify-center gap-2 
            ${product.stock > 0 
              ? 'bg-blue-600 hover:bg-blue-700 text-white' 
              : 'bg-gray-300 cursor-not-allowed'}`}
        >
          <ShoppingCart size={20} />
          {product.stock > 0 ? 'Agregar al carrito' : 'Sin stock'}
        </button>

        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="w-full mt-2 text-gray-600 flex items-center justify-center gap-1 hover:text-gray-800"
        >
          {isExpanded ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
          {isExpanded ? 'Menos detalles' : 'Más detalles'}
        </button>

        {isExpanded && (
          <div className="mt-4 space-y-2 text-sm text-gray-600">
            <p>{product.longDescription}</p>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <span className="font-semibold">Marca:</span> {product.brand}
              </div>
              <div>
                <span className="font-semibold">Categoría:</span> {product.category}
              </div>
              {product.ageFrom && product.ageTo && (
                <div>
                  <span className="font-semibold">Edad recomendada:</span>
                  {` ${product.ageFrom}-${product.ageTo} años`}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
