import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import { ShoppingCart, ChevronDown, ChevronUp } from 'lucide-react';
import Button from '../common/Button';

const ProductCard = ({ product }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      {/* Imagen del producto */}
      <div className="relative h-48">
        <img 
          src={product.imageUrl} 
          alt={product.name}
          className="w-full h-full object-cover"
        />
        {product.freeShipping && (
          <span className="absolute top-2 right-2 bg-green-500 text-white px-2 py-1 rounded-full text-xs">
            Envío gratis
          </span>
        )}
      </div>

      {/* Información del producto */}
      <div className="p-4">
        <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-2">{product.shortDescription}</p>
        
        <div className="flex justify-between items-center mb-4">
          <span className="text-2xl font-bold">${product.price}</span>
          {product.stock > 0 ? (
            <span className="text-sm text-green-600">Stock: {product.stock}</span>
          ) : (
            <span className="text-sm text-red-600">Sin stock</span>
          )}
        </div>

        <Button
          variant="primary"
          fullWidth
          onClick={handleAddToCart}
          disabled={product.stock === 0}
        >
          <ShoppingCart className="mr-2" size={20} />
          {product.stock > 0 ? 'Agregar al carrito' : 'Sin stock'}
        </Button>

        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="w-full mt-4 text-gray-600 flex items-center justify-center gap-1 hover:text-gray-800"
        >
          {isExpanded ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
          {isExpanded ? 'Menos detalles' : 'Más detalles'}
        </button>

        {isExpanded && (
          <div className="mt-4 space-y-2 text-sm text-gray-600">
            <p>{product.longDescription}</p>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <span className="font-semibold">Marca:</span> {product.brand}
              </div>
              <div>
                <span className="font-semibold">Categoría:</span> {product.category}
              </div>
              {product.ageFrom && product.ageTo && (
                <div className="col-span-2">
                  <span className="font-semibold">Edad recomendada:</span>
                  {` ${product.ageFrom}-${product.ageTo} años`}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
