import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import { X, Plus, Minus } from 'lucide-react';
import { useNotification } from '../../context/NotificationContext';
import { cartService } from '../../services/api';

const CartModal = ({ isVisible, closeModal }) => {
  const navigate = useNavigate();
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity, 
    getTotal,
    clearCart 
  } = useCart();
  const { showNotification } = useNotification();

  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === 'Escape') closeModal();
    };
    window.addEventListener('keydown', handleEsc);
    return () => window.removeEventListener('keydown', handleEsc);
  }, [closeModal]);

  const handleCheckout = async () => {
    try {
      // Verificar que el carrito no esté vacío
      if (cartItems.length === 0) {
        showNotification('El carrito está vacío', 'error');
        return;
      }

      // Llamar al endpoint de crear preferencia en el backend
      const response = await cartService.createCheckout(cartItems);
      
      // Redirigir a la URL de pago de MercadoPago
      if (response.data.init_point) {
        window.location.href = response.data.init_point;
      } else {
        showNotification('Error al generar el pago', 'error');
      }
    } catch (error) {
      console.error('Error en checkout:', error);
      showNotification('Error al iniciar el pago', 'error');
    }
  };

  if (!isVisible) return null;

  return (
    <>
      {/* Overlay */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 z-50"
        onClick={closeModal}
      />
      
      {/* Modal */}
      <div className="fixed inset-0 z-50 overflow-y-auto">
        <div className="flex min-h-full items-center justify-center p-4">
          <div 
            className="relative bg-white rounded-lg shadow-xl w-full max-w-md"
            onClick={e => e.stopPropagation()}
          >
            {/* Header */}
            <div className="flex justify-between items-center p-4 border-b bg-gray-800 text-white rounded-t-lg">
              <h2 className="text-xl font-bold">Carrito de Compras</h2>
              <button 
                onClick={closeModal}
                className="text-white hover:text-gray-300 p-1"
              >
                <X size={24} />
              </button>
            </div>

            {/* Content */}
            <div className="p-4">
              {cartItems.length === 0 ? (
                <p className="text-center text-gray-800 py-8 text-lg">
                  El carrito está vacío
                </p>
              ) : (
                <div className="space-y-4">
                  {cartItems.map((item) => (
                    <div 
                      key={item.id} 
                      className="flex items-center space-x-4 bg-gray-100 p-4 rounded-lg"
                    >
                      {/* Imagen del producto */}
                      <img 
                        src={item.imageUrl} 
                        alt={item.name}
                        className="w-20 h-20 object-cover rounded"
                      />
                      
                      {/* Detalles del producto */}
                      <div className="flex-1">
                        <h3 className="font-bold text-gray-800">{item.name}</h3>
                        <p className="text-gray-800 font-medium">${item.price}</p>
                        
                        {/* Controles de cantidad */}
                        <div className="flex items-center space-x-2 mt-2">
                          <button 
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            disabled={item.quantity <= 1}
                            className="p-1 rounded-full bg-gray-300 hover:bg-gray-400 disabled:opacity-50 text-gray-800"
                          >
                            <Minus size={16} />
                          </button>
                          <span className="w-8 text-center text-gray-800 font-medium">{item.quantity}</span>
                          <button 
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            className="p-1 rounded-full bg-gray-300 hover:bg-gray-400 text-gray-800"
                          >
                            <Plus size={16} />
                          </button>
                        </div>
                      </div>

                      {/* Subtotal y botón eliminar */}
                      <div className="text-right">
                        <p className="font-bold text-gray-800">${(item.price * item.quantity).toFixed(2)}</p>
                        <button 
                          onClick={() => removeFromCart(item.id)}
                          className="text-red-600 hover:text-red-800 mt-2 font-medium"
                        >
                          Eliminar
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Footer */}
            {cartItems.length > 0 && (
              <div className="border-t p-4 space-y-4 bg-gray-100">
                <div className="flex justify-between text-lg font-bold text-gray-800">
                  <span>Total:</span>
                  <span>${getTotal().toFixed(2)}</span>
                </div>
                <div className="space-y-2">
                  <button 
                    className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors font-bold"
                    onClick={handleCheckout}
                    disabled={cartItems.length === 0}
                  >
                    Finalizar Compra
                  </button>
                  <button 
                    className="w-full bg-red-600 text-white py-2 rounded-lg hover:bg-red-700 transition-colors font-bold"
                    onClick={clearCart}
                  >
                    Vaciar Carrito
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default CartModal;
