import React, { useState } from 'react';
import { useNotification } from '../context/NotificationContext';
import { contactService } from '../services/api';
import Input from '../components/common/Input';
import Button from '../components/common/Button';
import { Mail, Phone, MapPin, Send } from 'lucide-react';

function Contacto() {
  const { showNotification } = useNotification();
  const [formValues, setFormValues] = useState({
    name: '',
    email: '',
    message: '',
  });

  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
    if (touched[name]) {
      validateField(name, value);
    }
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched({ ...touched, [name]: true });
    validateField(name, formValues[name]);
  };

  const validateField = (name, value) => {
    const newErrors = { ...errors };

    switch (name) {
      case 'name':
        if (!value) {
          newErrors.name = 'El nombre es requerido';
        } else if (value.length < 2) {
          newErrors.name = 'El nombre debe tener al menos 2 caracteres';
        } else {
          delete newErrors.name;
        }
        break;

      case 'email':
        if (!value) {
          newErrors.email = 'El email es requerido';
        } else if (!/\S+@\S+\.\S+/.test(value)) {
          newErrors.email = 'El email no es válido';
        } else {
          delete newErrors.email;
        }
        break;

      case 'message':
        if (!value) {
          newErrors.message = 'El mensaje es requerido';
        } else if (value.length < 10) {
          newErrors.message = 'El mensaje debe tener al menos 10 caracteres';
        } else {
          delete newErrors.message;
        }
        break;

      default:
        break;
    }

    setErrors(newErrors);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Marcar todos los campos como tocados
    const allTouched = Object.keys(formValues).reduce(
      (acc, key) => ({ ...acc, [key]: true }), {}
    );
    setTouched(allTouched);

    // Validar todos los campos
    Object.keys(formValues).forEach(key => {
      validateField(key, formValues[key]);
    });

    // Si hay errores, no enviar
    if (Object.keys(errors).length > 0) {
      showNotification('Por favor, corrija los errores del formulario', 'error');
      return;
    }

    try {
      setLoading(true);
      await contactService.sendContact(formValues); // Aquí se corrige el método
      showNotification('Mensaje enviado exitosamente', 'success');
      setSubmitted(true);
    } catch (error) {
      showNotification(error.message || 'Error al enviar el mensaje', 'error');
    } finally {
      setLoading(false);
    }
  };

  if (submitted) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full text-center">
          <div className="mb-4 text-green-500">
            <Send className="h-16 w-16 mx-auto" />
          </div>
          <h2 className="text-2xl font-bold mb-4">¡Gracias por contactarnos!</h2>
          <p className="text-gray-600">
            Nos comunicaremos contigo pronto.
          </p>
          <Button
            variant="primary"
            fullWidth
            className="mt-6"
            onClick={() => window.location.href = '/'}
          >
            Volver al inicio
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      {/* El contenido restante no se modifica */}
    </div>
  );
}

export default Contacto;
